mapper.select:
SELECT l.leave_id, l.user_id, l.message, l.to_user_id, l.link_leave_id, l.updata_time, l.delect_is, u.user_name, u.user_type, u.user_image, u1.user_name u1name, u1.user_type u1type, u1.user_image u1image FROM `leave` l LEFT JOIN `user` u ON l.user_id = u.user_id LEFT JOIN `user` u1 ON l.to_user_id = u1.user_id WHERE 1 = 1
AND leave_id = #{leave_id}
AND l.user_id = #{user_id}
AND message = #{message}
AND to_user_id = #{to_user_id}
AND link_leave_id = #{link_leave_id}
AND delect_is = #{delect_is}

SELECT l.leave_id, l.user_id, l.message, l.to_user_id, l.link_leave_id, l.updata_time, l.delect_is, u.user_name, u.user_type, u.user_image, u1.user_name u1name, u1.user_type u1type, u1.user_image u1image FROM `leave` l LEFT JOIN `user` u ON l.user_id = u.user_id LEFT JOIN `user` u1 ON l.to_user_id = u1.user_id WHERE 1=1
AND leave_id = #{leave_id}
AND l.user_id = #{user_id}
AND message = #{message}
AND to_user_id = #{to_user_id}
AND link_leave_id = #{link_leave_id}
AND delect_is = #{delect_is}
AND link_leave_id IS NULL
